text = ""
x=list(text)
freqs = {}
hidden_word=''
for line in x:
    for char in line:
        if char in freqs:
            freqs[char] +=1
        else:
            freqs[char] =1
for key, value in enumerate(text):
    print(key,value)
for index,char in enumerate(text):
    if text[index-1]=='P' and text[index+1]=='K':
        hidden_word +=char
print(hidden_word)


